import { Col, Image, Row } from "antd";
import React from "react";
import { useState } from "react";
import { LeftOutlined, RightOutlined } from "@ant-design/icons";
import CustomButton from "components/custom-button";
import CustomModal from "components/CustomModal/CustomModal";

const AuctionImageViewer = ({ images, show, toggleShow }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  let newImageArray = [];
  if (images.length > 0) {
    const cover = images.findIndex((el) => el.fileType === "image");
    newImageArray = [
      images[cover],
      ...images.filter((el) => el.fileType !== "image" ),
      ...images.filter((el) => el?.fileType !== "video"),
    ];
  }

  const currentMedia = newImageArray[currentIndex];

  const changeImage = (index, isPrevious) => () =>
    setCurrentIndex(isPrevious ? index - 1 : index + 1);

  return (
    <CustomModal
      visibility={show}
      handleCancel={() => toggleShow(false)}
      className={"auction-image-viewer-modal"}
    >
      <Row
        align={"middle"}
        justify={"space-between"}
        gutter={[10, 10]}
        wrap={false}
      >
        <Col>
          <CustomButton
            icon={<LeftOutlined />}
            disabled={currentIndex === 0}
            onClick={changeImage(currentIndex, true)}
          />
        </Col>
        <Col>
          {currentMedia?.fileType === "video" ? (
            <video src={currentMedia?.file} controls width={"100%"} />
          ) : (
            <Image
              preview={false}
              src={currentIndex >= 0 ? currentMedia?.file : undefined}
              width={"40vw"}
              style={{ aspectRatio: 1, objectFit: "cover" }}
              fallback="data:image/png;base64,"
            />
          )}
        </Col>
        <Col>
          <CustomButton
            icon={<RightOutlined />}
            disabled={currentIndex === images.length - 1}
            onClick={changeImage(currentIndex, false)}
            type={"default"}
          />
        </Col>
      </Row>
    </CustomModal>
  );
};

export default AuctionImageViewer;
